function edit(userID){
    window.location.href = applicationPath + 'users/edit?user_id=' + userID ;
}

function delete_operator(userID){

    var x = confirm('Are you sure you want to delete this operator?');
    if ( x === true){
        $.ajax({
            url: applicationPath + "modules/users/ajax/delete_operator.php",
            type: 'POST',
            data: {user_id: userID},
            cache: false,
            success: function (data) {
                if (data === 'true'){
                    validNavigation = true;
                    window.location.href = applicationPath + 'users' ;
                }else{
                    $('#error_handling').html(data);
                }
            }
        });
    }
}