                         $.ajax({                            type:"post",                            url:"ajax_company.php",                            data:"action=company_search&title="+title,                            success:function(data){                                $(".loadmore").html(data);                                                            }                          });                      } else{                                  $(".errorMsg").remove();                            $('#search').after('<label style="color:#990000;" class="errorMsg">Company Cannot be empty.</label>');                            $(".errorMsg").slideDown(500);                            setTimeout('$(".errorMsg").slideUp("slow")', 5000);                            return false;                      }                      }                   $("#button").click(function(){                     search();                  });            });        </script>                  </div></body></html>