ymodal").show();			}		    });		}		return false;	    });	});    </script>     <!-- Add Employee script's     Added by Abbishek-->    <script>	function current_working(e) {	    var x = "1";	    var x1 = "0";	    var myid = $(e).attr("id");	    if ($('#' + myid).is(':checked')) {		$('.date_to').css({'display': 'none'});		$('#Tojob').val('');		$('#' + myid).val(x);	    } else {		$('.date_to').css({'display': 'block'});		$('#' + myid).val(x1);	    }	}	$(document).ready(function () {	    $("#btnaddjobexper").click(function () {		var employer = $("#Employer").val();		var position = $("#Position").val();		var from = $("#Fromjob").val();		var to = $("#Tojob").val();		var form_month = from.trim().substring(0, 2);		var to_month = to.trim().substring(0, 2);		var form_year = from.trim().substring(3);		var to_year = to.trim().substring(3);		var current = $("#currentlyworking").val();		//alert(form_month);		//alert(to_month);		//alert(form_year);		//alert(to_year);		// Returns successful data submission message when the entered information is stored in database.		var dataString = '&employer1=' + employer + '&position1=' + position + '&from1_month=' + form_month + '&to1_month=' + to_month + '&from1_year=' + form_year + '&to1_year=' + to_year + '&current1=' + current;		if (employer == '' || position == '' || from == '' || current == '')		{		    swal(			    '',			    'Please Fill All Fields!',			    'warning'			    )		} else		{		    // alert(dataString);		    // AJAX Code To Submit Form.		    $.ajax({			type: "POST",			url: "seeker_extra_details_inner.php?op=addjobdetails",			data: dataString,			cache: false,			success: function (response) {			    response = $.trim(response);			    if (response == 1)			    {				swal(					'',					'Added Successfully!',					'success'					)				$('.swal2-confirm').click(function () {				    location.reload();				});				$('.overlaymodal').hide();			    }			    //window.location 