te.setFullYear(minDate.getFullYear() + 1);	    $('#ToProject').datepicker('setStartDate', minDate);	});	$('#ToProject').datepicker({	    autoclose: true,	    minViewMode: 2,	    format: 'yyyy'	}).on('changeDate', function (selected) {	    var maxDate = new Date(selected.date.valueOf());	    $('#FromProject').datepicker('setEndDate', maxDate);	});	$("#From, #To, #FromProject, #ToProject, #Fromjob, #Tojob").keydown(false);    </script>    <script type="text/javascript">	$(function () {	    $('#college, #Employer, #Position').keydown(function (e) {		var key = e.keyCode;		if (!((key == 8) || (key == 32) || (key == 46) || (key >= 35 && key <= 40) || (key >= 65 && key <= 90))) {		    e.preventDefault();		    return false;		}	    });	    $("#cgpa, #percentage").keypress(function (e) {		var regex = new RegExp("^[0-9.%]+$");		var key = String.fromCharCode(event.charCode == event.which ? event.which : event.charCode);		if (!regex.test(key)) {		    event.preventDefault();		    return false;		}	    });	});    </script></div>    </body></html>